mapper.select:
select o.ordernum as ordernum ,o.selectionnum as selectionnum ,m.id as id ,to_char(o.orderdate,'yyyy-mm-dd hh24miss') as orderdate ,o.bookcover as bookcover ,o.currentstate as currentstate ,o.price as price from orderbook o, myselection m where o.selectionnum = m.selectionnum and
o.selectionnum = #{selectionnum} and o.currentstate not in('addToCart','makeOrder','confirmedOrder')
o.ordernum = #{ordernum}

mapper.update:
update
	orderbook
set
	orderdate=sysdate
	
	,bookcover=#{bookcover}
	
	,currentstate=#{currentstate}
	,price=#{price}
where
	ordernum=#{ordernum}
